import React, { Component } from 'react'

export class Question extends Component {

  state = {

  }

  handleSelection = (e) => {
    console.log(e.target.value)
  }

  render() {
    return (
      <div className="question-container">
        <p className="question-text">{this.props.text}</p>
        <button className="game-button question-button" value="true" onClick={this.handleSelection}>True</button>
        <button className="game-button question-button" value="false" onClick={this.handleSelection}>False</button>
      </div>
    )
  }
}

export default Question
