import React, { Component } from 'react';

export class Question extends Component {
  state = {
    selectionMade: false,
    selectedAnswer: null,
    trueBtn: null
  };

  handleSelection = (e) => {


    this.setState(
      {
        selectionMade: true,
        selectedAnswer: e.target.value,
        trueBtn: this.state.selectedAnswer === this.props.answer.toString() ? true : false,
        falseBtn: this.state.selectedAnswer === this.props.answer.toString() ? true : false
      },
    );
  };

  checkAnswer = () => {
    console.log(`correct answer: `, this.state.selectedAnswer === this.props.answer.toString()
    ? true
    : false)
    return this.state.selectedAnswer === this.props.answer.toString()
      ? true
      : false;
  };

  render() {
    // console.log(this.props, this.state);
    return (
      <div className="question-container">
        <p className="question-text">{this.props.text}</p>
        <button
          className={`game-button question-button ${this.state.trueBtn ? 'correct' : 'inCorrect'}`}
          disabled={this.state.selectionMade}
          value="true"
          onClick={this.handleSelection}
        >
          True
        </button>
        <button
          className={`game-button question-button ${this.state.falseBtn ? 'correct' : 'inCorrect'}`}
          disabled={this.state.selectionMade}
          value="false"
          onClick={this.handleSelection}
        >
          False
        </button>
      </div>
    );
  }
}

export default Question;
