import React from 'react'

function Question(props) {
  return (
    <div className="question-container">
      <p className="question-text">{props.text}</p>
      <button className="game-button question-button" value="true" onClick={(e) => props.handleSelection(e)}>True</button>
      <button className="game-button question-button">False</button>
    </div>
  )
}

export default Question
