import React, { Component } from 'react';

export class Question extends Component {
  state = {
    selectionMade: false,
    selectedAnswer: null,
  };

  handleSelection = (e) => {
    this.setState(
      {
        selectionMade: true,
        selectedAnswer: e.target.value,
      },
      (e) => this.checkAnswer(e)
    );
  };

  checkAnswer = (e) => {
    console.log('checking answer', (this.state.selectedAnswer === this.props.answer.toS));
  };

  render() {
    // console.log(this.props, this.state);
    return (
      <div className="question-container">
        <p className="question-text">{this.props.text}</p>
        <button
          className={`game-button question-button ${
            this.state.selectionMade &&
            this.state.selectedAnswer === this.props.answer.toString()
              ? 'correct'
              : ''
          }`}
          disabled={this.state.selectionMade}
          value="true"
          onClick={this.handleSelection}
        >
          True
        </button>
        <button
          className={`game-button question-button ${
            this.state.selectionMade &&
            this.state.selectedAnswer === this.props.answer.toString()
              ? 'correct'
              : ''
          }`}
          disabled={this.state.selectionMade}
          value="false"
          onClick={this.handleSelection}
        >
          False
        </button>
      </div>
    );
  }
}

export default Question;
