import React, { Component } from 'react'

export class Question extends Component {

  state = {
    selectionMade: false
  }

  handleSelection = (e) => {
    console.log(e.target.value)
    this.setState({ selectionMade: true })
  }

  render() {
    return (
      <div className="question-container">
        <p className="question-text">{this.props.text}</p>
        <button className="game-button question-button" disabled={this.state.selectionMade} value="true" onClick={this.handleSelection}>True</button>
        <button className="game-button question-button" disabled={this.state.selectionMade} value="false" onClick={this.handleSelection}>False</button>
      </div>
    )
  }
}

export default Question
